const d=document.getElementById("show-dl-options"),l=document.getElementById("show-dl-options-trigger"),e=document.getElementsByClassName("download-button");l.addEventListener("click",()=>{const o=document.getElementById("dl-options");d.classList.add("hidden"),o.classList.remove("hidden")});for(let o=0;o<e.length;o++)e[o].addEventListener("click",()=>{const n=document.querySelectorAll(".download-usp"),t=document.getElementById("download-bonus-guide");n.forEach(function(s){s.classList.add("scale-0")}),t.classList.remove("scale-0"),t.classList.add("scale-100")});