const t=document.getElementById("toc-modal"),c=document.getElementsByClassName("toc-link");for(let e=0;e<c.length;e++){const o=c[e];o.addEventListener("click",()=>{document.getElementById(o.getAttribute("data-target")).scrollIntoView(),t.close()})}const n=document.getElementById("open-toc");n.addEventListener("click",()=>{t.showModal()});const l=document.getElementById("close-toc");l.addEventListener("click",()=>{t.close()});